import React, { Component } from 'react';
import axios from 'axios';
import { users } from './UserFunctions';

class Search extends Component {
    constructor() {
        super();
        this.state = {
            first_name: '',
            last_name: '',
            returnedUsers: [],
            users: []
        }

        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onChange(e) {
        this.setState({[e.target.name]: e.target.value});
    }

    getUsers = _  => {
        axios
        .get('users')
        .then(res => {
            this.setState({ users: res.data });
        })
        .catch(err => console.log("Error: " + err))
        return this.state.users;
    }

    onSubmit(e) {
        e.preventDefault();

        this.getUsers().map(user => {
            const firstName = user.first_name;
            const lastName = user.last_name;
            if (this.state.first_name == firstName && this.state.last_name == lastName) {
                console.log(user);
            } else {
                console.log(`User ${user.id} does not match`);
            }
        })

    }

    render() { 
        return (
            <div className="container">
                <div className="row">
                    <div className="col-md-6 mt-5 mx-auto">
                        <form noValidate onSubmit={this.onSubmit}>
                            <h1 className="h1 mb-3 font-weight-normal text-center">Search For a User</h1>
                            <div className="form-group">
                                <label htmlFor="first_name">First Name</label>
                                <input type="text"
                                 className="form-control"
                                 name="first_name"
                                 placeholder="Enter First Name"
                                 value={this.state.first_name}
                                 onChange={this.onChange} />
                            </div>
                            <div className="form-group">
                                <label htmlFor="last_name">Last Name</label>
                                <input type="text"
                                 className="form-control"
                                 name="last_name"
                                 placeholder="Enter Last Name"
                                 value={this.state.last_name}
                                 onChange={this.onChange} />
                            </div>
                            <button 
                            type="submit"
                            className="btn btn-lg mt-5 btn-danger btn-block">
                                Search
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default Search;
